.has__success .message__validation {
  color: #28a745;
}

.has__warning .form-control,
.has__warning .v-file-label {
  border-color: #ffc107;
}
.has__warning .v-file-button {
  background: #ffc107;
  color: #000;
}
.has__warning .message__validation {
  color: #ffc107;
}

.has__error .form-control,
.has__error .v-file-label {
  border-color: #dc3545;
