.has__error .v-file-button {
  background: #dc3545;
  color: #FFF;
}
.has__error .message__validation {
  color: #dc3545;
}

.v-file {
  position: relative;
}

.v-file-input {
  position: relative;
  z-index: 2;
  width: 100%;
  height: calc(1.5em + 0.75rem + 2px);
  margin: 0;
  display: block;
  opacity: 0;
  cursor: pointer;
}

.v-file-label {
  position: absolute;
  width: 100%;
  height: 100%;
  display: flex;
  border: 1px solid #ced4da;
  line-height: 1.5;
  font-size: 1rem;
  padding: 0.375rem 0.75rem;
  font-weight: 400;
  border-radius: 0.25rem;
